INSERT INTO episodes VALUES (473280, 20037, 21, 'Touch of Death', 'Touch of Death', 'Le NCIS et le Hawaii 5-0 doivent retourner aux Etats Unis afin de suivre un suspect qui menace de relâcher un virus mortel sur le continent.', 0.0, false, false);
