CREATE FUNCTION episodedowloaded() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	x float;
	y float;
	z float;
BEGIN

	x := (	SELECT COUNT(*) FROM episodes
			WHERE season_id = new.season_id AND downloaded = true);
	y := (	SELECT COUNT(*) FROM episodes
			WHERE season_id = new.season_id);
	z := (x / y) * 100;
	
	UPDATE seasons SET downloaded = z
	WHERE id = new.season_id;

	RETURN new;
END;$$;


