CREATE FUNCTION seasondownloaded() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	x float;
BEGIN
	
	IF old.downloaded != 100 AND new.downloaded = 100 THEN
		UPDATE episodes SET downloaded = true
		WHERE season_id = new.id;
	ELSIF old.downloaded != 0 AND new.downloaded = 0 THEN
		UPDATE episodes SET downloaded = false
		WHERE season_id = new.id;
	END IF;

	x := (	SELECT AVG(downloaded) FROM seasons
			WHERE serie_id = new.serie_id);
	
	UPDATE series SET downloaded = x
	WHERE id = new.serie_id;

	RETURN new;
END;$$;


